mapper.select:
SELECT

FROM
    KAOS.ESCORT_MAIN_INFO T1
WHERE
    T1.PATIENT_CARD_NO = #{patientCardNo}
    AND T1.HAPPEN_NO = #{happenNo}
    AND T1.HELPER_CARD_NO = #{helperCardNo}
    AND T1.OPER_DATE = (
        SELECT
            MAX(OPER_DATE)
        FROM
            KAOS.ESCORT_MAIN_INFO
        WHERE
            T1.PATIENT_CARD_NO = #{patientCardNo}
            AND T1.HAPPEN_NO = #{happenNo}
            AND T1.HELPER_CARD_NO = #{helperCardNo}
    )
